graphql.query:
mutation authRegister($input: RegisterInput!) {
    authRegister(input: $input) {
        user {
            id
            name
            email
        }
        token {
            accessToken
            refreshToken
        }
    }
}

mutation authLogin($input: LoginInput!) {
    authLogin(input: $input) {
        user {
            name
            email
        }
        token {
            accessToken
            refreshToken
        }
    }
}

mutation authVerifyEmail($userId: ID!, $verifyToken: String!) {
    authVerifyEmail(userId: $userId, verifyToken: $verifyToken)
}

mutation authResetPassword($userId: ID!, $resetToken: String!, $password: String!) {
    authResetPassword(userId: $userId, resetToken: $resetToken, password: $password)
}

mutation authUpdatePassword($oldPassword: String!, $newPassword: String!) {
    authUpdatePassword(oldPassword: $oldPassword, newPassword: $newPassword)
}

mutation userCreate($input: UserDataInput!){
    userCreate(input: $input) {
        name
        email
    }
}

query user($userId: ID!) {
    user(userId: $userId) {
        id
        name
        email
    }
}

query users($pagination: PaginationInput!) {
    users(pagination: $pagination) {
        users {
            id
            name
            createdAt
        }
        pagination {
            total
            next
            hasNext
        }
    }
}

mutation userUpdateMe($input: UserDataInput!) {
    userUpdateMe(input: $input) {
        id
        name
        email
    }
}

mutation userUpdate($userId: ID!, $input: UserDataInput!) {
    userUpdate(userId: $userId, input: $input) {
        id
        name
        role
        image
        email
    }
}

mutation userDelete($userId: ID!) {
    userDelete(userId: $userId) {
        id
        name
        email
    }
}
